import React, { useState } from 'react';
import './css/SettingsPage.css'; // Import CSS cho trang Settings
import { Link } from 'react-router-dom';

const Settings = () => {
  // Các trạng thái cho các trường nhập liệu
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [theme, setTheme] = useState('light'); // light or dark theme
  const [currentTab, setCurrentTab] = useState('personal-info'); // Tab hiện tại

  // Hàm xử lý khi người dùng lưu các thay đổi
  const handleSaveChanges = (e) => {
    e.preventDefault();
    console.log('Saved changes:', { username, email, password, theme });
    alert('Your changes have been saved!');
  };

  // Sidebar Items
  const sidebarItems = [
    { id: 'personal-info', label: 'Chỉnh sửa thông tin cá nhân' },
    { id: 'change-password', label: 'Thay đổi mật khẩu' },
    { id: 'privacy-settings', label: 'Quyền riêng tư' },
    { id: 'social-accounts', label: 'Liên kết tài khoản mạng xã hội' },
  ];

  // Nội dung từng tab
  const renderContent = () => {
    switch (currentTab) {
      case 'personal-info':
        return (
          <form onSubmit={handleSaveChanges}>
            <div className="form-group">
              <label htmlFor="username">Username</label>
              <input
                type="text"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Enter your username"
              />
            </div>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your email"
              />
            </div>
            <button type="submit" className="save-btn">Save Changes</button>
          </form>
        );
      case 'change-password':
        return (
          <form onSubmit={handleSaveChanges}>
            <div className="form-group">
              <label htmlFor="password">New Password</label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter your new password"
              />
            </div>
            <button type="submit" className="save-btn">Change Password</button>
          </form>
        );
      case 'privacy-settings':
        return (
          <div>
            <h2>Privacy Settings</h2>
            <p>Adjust your privacy settings here...</p>
            {/* Add privacy settings form */}
          </div>
        );
      case 'social-accounts':
        return (
          <div>
            <h2>Social Accounts</h2>
            <p>Link or unlink your social media accounts...</p>
            {/* Add social accounts linking form */}
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="settings-page">
      <header>
        <div className="container">
          <Link to="/" className="logo">LearnLinguaAI</Link>
          <nav>
            <ul>
              <li><Link to="/">Home</Link></li>
              <li><Link to="/study">Exercises</Link></li>
              <li><Link to="/about" className="active">About</Link></li>
              <li className="dropdown">
                <a href="#" className="dropbtn">See More</a>
                <div className="dropdown-content">
                  <Link to="/user-profile">Profile</Link>
                  <Link to="/contact">Contact</Link>
                  <a href="#" id="logout-link">Logout</a>
                </div>
              </li>
            </ul>
          </nav>
        </div>
      </header>

      <section id="settings-section">
        <div className="container">
          <div className="settings-container">
            {/* Sidebar */}
            <aside className="sidebar">
              <ul>
                {sidebarItems.map(item => (
                  <li
                    key={item.id}
                    className={currentTab === item.id ? 'active' : ''}
                    onClick={() => setCurrentTab(item.id)}
                  >
                    {item.label}
                  </li>
                ))}
              </ul>
            </aside>

            {/* Main Content */}
            <div className="settings-content">
              {renderContent()}
            </div>
          </div>
        </div>
      </section>

      <footer>
        <div className="container">
          <p>&copy; 2024 LearnLinguaAI. All Rights Reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default Settings;
