import React, { useEffect, useState } from 'react';

const Chatbot = () => {
  const [isChatbotVisible, setIsChatbotVisible] = useState(true);

  useEffect(() => {
    // Function to load the Chatbase script
    const loadChatbaseScript = () => {
      const existingScript = document.getElementById('chatbase-script');
      if (!existingScript) {
        const script = document.createElement('script');
        script.src = 'https://www.chatbase.co/embed.min.js';
        script.async = true;
        script.defer = true;
        script.id = 'chatbase-script';
        script.setAttribute('chatbotId', 'WqpEHCHu7nrwmQ1VjPBDX');
        script.setAttribute('domain', 'www.chatbase.co');

        document.body.appendChild(script);
      }
    };

    // Load the script when the component mounts
    loadChatbaseScript();

    // Optional cleanup if component unmounts
    return () => {
      const script = document.getElementById('chatbase-script');
      if (script) {
        document.body.removeChild(script);
      }
    };
  }, []);

  // Toggle chatbot visibility
  const toggleChatbot = () => {
    setIsChatbotVisible(!isChatbotVisible);
  };

  return (
    <div style={{ position: 'relative', textAlign: 'center', padding: '20px' }}>
      {/* Header Section */}
      <header style={{ marginBottom: '20px', fontSize: '24px', fontWeight: 'bold' }}>
        <h1>AI Chatbot</h1>
        <p>Interact with our AI chatbot to get help with learning English.</p>
        <button onClick={toggleChatbot} style={{ padding: '10px 20px', fontSize: '16px', marginTop: '10px' }}>
          {isChatbotVisible ? 'Hide Chatbot' : 'Show Chatbot'}
        </button>
      </header>

      {/* Chatbot iframe */}
      {isChatbotVisible && (
        <iframe
          src="https://www.chatbase.co/chatbot-iframe/WqpEHCHu7nrwmQ1VjPBDX"
          width="100%"
          style={{
            height: '100%', minHeight: '700px', marginTop: '20px',
            borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)'
          }}
          frameBorder="0"
          title="AI Chatbot"
        ></iframe>
      )}
    </div>
  );
};

export default Chatbot;
