// src/components/Banner.js (updated)
import React from 'react';
import './Banner.css';

const Banner = () => {
  return (
    <div className="banner">
      <h1>Hệ thống AI Chatbot hỗ trợ học tiếng Anh</h1>
      <p>
        Khám phá cách học tiếng Anh hiệu quả hơn với hệ thống chatbot thông minh của chúng tôi. Nhận phản hồi về lỗi ngữ pháp, phát âm, và nhiều hơn nữa.
      </p>
      <a href="/signup" className="cta-button">Đăng ký ngay</a>
      <a href="/trial" className="cta-button secondary">Bắt đầu học thử</a>
    </div>
  );
};

export default Banner;
