import React, { useEffect, useState } from 'react';
// import './css/ChatbotPage.css'; // Import the CSS file for styling

const Chatbot = () => {
  const [isChatbotVisible, setIsChatbotVisible] = useState(true);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const loadChatbaseScript = () => {
      setIsLoading(true);  // Set loading state to true while script loads
      const existingScript = document.getElementById('chatbase-script');
      if (!existingScript) {
        const script = document.createElement('script');
        script.src = 'https://www.chatbase.co/embed.min.js';
        script.async = true;
        script.defer = true;
        script.id = 'chatbase-script';
        script.setAttribute('chatbotId', 'WqpEHCHu7nrwmQ1VjPBDX');
        script.setAttribute('domain', 'www.chatbase.co');

        script.onload = () => setIsLoading(false); // Stop loading when script is loaded
        document.body.appendChild(script);
      }
    };

    loadChatbaseScript();

    return () => {
      const script = document.getElementById('chatbase-script');
      if (script) {
        document.body.removeChild(script);
      }
    };
  }, []);

  const toggleChatbot = () => {
    setIsChatbotVisible(!isChatbotVisible);
  };

  return (
    <div className={`chatbot-container ${isLoading ? 'loading' : ''}`}>
      <button className="chatbot-toggle-btn" onClick={toggleChatbot}>
        {isChatbotVisible ? 'Hide Chatbot' : 'Show Chatbot'}
      </button>

      {isLoading && (
        <div className="chatbot-loading">
          <div className="spinner"></div> Loading...
        </div>
      )}

      {isChatbotVisible && !isLoading && (
        <iframe
          src="https://www.chatbase.co/chatbot-iframe/WqpEHCHu7nrwmQ1VjPBDX"
          className="chatbot-iframe"
          title="AI Chatbot"
        ></iframe>
      )}
    </div>
  );
};

export default Chatbot;
