import React, { Link,useEffect, useState } from 'react';

const Chatbot = () => {
  const [isChatbotVisible, setIsChatbotVisible] = useState(true);

  const navigate = useNavigate();

  // Logout functionality
  const handleLogout = (event) => {
    event.preventDefault();
    localStorage.removeItem("loggedIn");
    navigate("/login"); // Redirect to login pages after logout
  };

  useEffect(() => {
    // Function to load the Chatbase script
    const loadChatbaseScript = () => {
      const existingScript = document.getElementById('chatbase-script');
      if (!existingScript) {
        const script = document.createElement('script');
        script.src = 'https://www.chatbase.co/embed.min.js';
        script.async = true;
        script.defer = true;
        script.id = 'chatbase-script';
        script.setAttribute('chatbotId', 'WqpEHCHu7nrwmQ1VjPBDX');
        script.setAttribute('domain', 'www.chatbase.co');

        document.body.appendChild(script);
      }
    };

    // Load the script when the component mounts
    loadChatbaseScript();

    // Optional cleanup if component unmounts
    return () => {
      const script = document.getElementById('chatbase-script');
      if (script) {
        document.body.removeChild(script);
      }
    };
  }, []);

  // Toggle chatbot visibility
  const toggleChatbot = () => {
    setIsChatbotVisible(!isChatbotVisible);
  };

  return (
    <div style={{ position: 'relative', textAlign: 'center', padding: '20px' }}>
      {/* Header Section */}
      <header>
        <div className="container">
          <Link to="/" className="logo">LearnLinguaAI</Link>
          <nav>
            <ul>
              <li><Link to="/" className="active">Home</Link></li>
              <li><Link to="/classes">Classes</Link></li>
              <li><Link to="/about">About</Link></li>
              <li className="dropdown">
                <a href="#" className="dropbtn">See More</a>
                <div className="dropdown-content">
                  <Link to="/user-profile">Profile</Link>
                  <Link to="/contact">Contact</Link>
                  <Link to="/setting">Setting</Link>
                  <Link to="/teacher">Teacher</Link>
                  <Link to="/chatbot">Chat</Link>
                  <a href="#" onClick={handleLogout}>Logout</a>
                </div>
              </li>
            </ul>
          </nav>
        </div>
      </header>
      {/* Chatbot iframe */}
      {isChatbotVisible && (
        <iframe
          src="https://www.chatbase.co/chatbot-iframe/WqpEHCHu7nrwmQ1VjPBDX"
          width="100%"
          style={{
            height: '100%', minHeight: '700px', marginTop: '20px',
            borderRadius: '10px', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)'
          }}
          frameBorder="0"
          title="AI Chatbot"
        ></iframe>
      )}
    </div>
  );
};

export default Chatbot;
