// src/components/LoginSignupPage.js
import React, { useState } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import './LoginSignupPage.css';

const LoginSignupPage = () => {
  const [isLogin, setIsLogin] = useState(true);

  // Validation schema using Yup
  const validationSchema = Yup.object({
    email: Yup.string().email('Email không hợp lệ').required('Vui lòng nhập email'),
    password: Yup.string().min(6, 'Mật khẩu phải có ít nhất 6 ký tự').required('Vui lòng nhập mật khẩu'),
    ...(isLogin ? {} : { confirmPassword: Yup.string().oneOf([Yup.ref('password'), null], 'Mật khẩu không khớp').required('Vui lòng xác nhận mật khẩu') }),
  });

  // Handle form submission
  const handleSubmit = (values, { setSubmitting }) => {
    setSubmitting(true);
    // Implement login or signup logic
    console.log(isLogin ? 'Đăng nhập với giá trị:' : 'Đăng ký với giá trị:', values);
    setTimeout(() => {
      setSubmitting(false);
    }, 2000);
  };

  return (
    <div className="login-signup-page">
      <div className="form-container">
        <h2>{isLogin ? 'Đăng Nhập' : 'Đăng Ký'}</h2>
        
        <Formik
          initialValues={{
            email: '',
            password: '',
            confirmPassword: '',
          }}
          validationSchema={validationSchema}
          onSubmit={handleSubmit}
        >
          {({ isSubmitting }) => (
            <Form>
              <div className="form-field">
                <label htmlFor="email">Email</label>
                <Field type="email" name="email" placeholder="Nhập email của bạn" />
                <ErrorMessage name="email" component="div" className="error-message" />
              </div>

              <div className="form-field">
                <label htmlFor="password">Mật khẩu</label>
                <Field type="password" name="password" placeholder="Nhập mật khẩu của bạn" />
                <ErrorMessage name="password" component="div" className="error-message" />
              </div>

              {!isLogin && (
                <div className="form-field">
                  <label htmlFor="confirmPassword">Xác nhận mật khẩu</label>
                  <Field type="password" name="confirmPassword" placeholder="Xác nhận mật khẩu của bạn" />
                  <ErrorMessage name="confirmPassword" component="div" className="error-message" />
                </div>
              )}

              <button type="submit" disabled={isSubmitting} className="btn-primary">
                {isLogin ? 'Đăng Nhập' : 'Đăng Ký'}
              </button>
            </Form>
          )}
        </Formik>

        <p className="toggle-text">
          {isLogin ? "Bạn chưa có tài khoản? " : 'Bạn đã có tài khoản? '}
          <button onClick={() => setIsLogin(!isLogin)} className="toggle-button">
            {isLogin ? 'Đăng ký ở đây' : 'Đăng nhập ở đây'}
          </button>
        </p>
      </div>
    </div>
  );
};

export default LoginSignupPage;
